import React from 'react'
import { Box, Typography,Hidden } from '@mui/material'
import Slider from "react-slick";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import { CardActionArea } from '@mui/material';
import {data, settings} from './CardData';

// card section

const CardSection = () => {

  return (
    // <>
    // <Box sx={{height:'100%',alignItems:'center'}}>
    <Slider {...settings} sx={{margin:'auto'}}>
      {/* card */}
   {data.map((value,index)=>(
   <div  key={index}>
   <Card sx={{backgroundColor: (value.bgcolor),height:"50vh",borderRadius:'29px',fontFamily:'Poppins',width:'70%',margin:'auto'}}>
     <CardActionArea>

       <CardContent>
         <Typography gutterBottom variant="h5" sx={{width:'10%',fontFamily:"Poppins",fontWeight:"bolder"}}>
          {value.title}
         </Typography>
         <Typography variant="body2" color="text.secondary" sx={{width:'80px',fontFamily:"Poppins",fontWeight:'300px'}}>
        {value.desc}
         </Typography>

       </CardContent> 
       <CardMedia
       component="img"
       sx={{ width: 151,marginLeft:"40px"}}
       image={value.img} 

     />
     </CardActionArea> 
   </Card>
   </div>
))}
  </Slider>

  //  </Box>
  //   </>
  )
}

export default CardSection



